import { combineReducers } from 'redux';
import { userLoginReducer, userRegisterReducer, userProfileReducer } from './user_reducer';
import { getAllBookReducer, addBookReducer } from './book_reducer.js';
import { issueBookReducer, returnBookReducer, issuedBooksReducer } from './issue_reducer';

const rootReducer = combineReducers({
  userLogin: userLoginReducer,
  userRegister: userRegisterReducer,
  userProfile: userProfileReducer,
  bookList: bookListReducer,
  addBook: addBookReducer,
  issueBook: issueBookReducer,
  returnBook: returnBookReducer,
  issuedBooks: issuedBooksReducer,
});

export default rootReducer;
